const audioDataString = items[0].json.AudioStream;

let base64Data;
if (typeof audioDataString === 'string') {
    base64Data = audioDataString;
} else if (audioDataString.type === 'Buffer') {
    base64Data = Buffer.from(audioDataString.data).toString('base64');
} else {
    base64Data = audioDataString;
}

const binaryData = {
    pollyAudio: {
        data: base64Data,
        mimeType: 'audio/mpeg',
        fileName: 'speech.mp3'
    }
};

return [{
    json: items[0].json,
    binary: binaryData
}];
